import React, { useState, useContext } from "react";
import { useHistory } from "react-router-dom"; // userHistory is for v5
import { AuthContext } from "../contexts/AuthContext";
import { registerUser } from "../api/auth";

const Register = () => {
  const [formData, setFormData] = useState({
    username: "",
    email: "",
    password: "",
  });

  const [error, setError] = useState("");
  const { login } = useContext(AuthContext); // Ensure this matches what your AuthContext exports (login or setAuth)

  const history = useHistory(); // Initialize history hook

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    try {
      const data = await registerUser(formData);

      // If backend returns a token on register, login immediately
      if (data.token) {
        login(data.token);
        history.push("/"); // Redirect to Home using v5 syntax
      } else {
        history.push("/login");
      }
    } catch (err) {
      setError(
        err.response?.data?.message || "Registration failed. Try again."
      );
    }
  };

  return (
    <div>
      <h2>Register</h2>
      {error && <p style={{ color: "red" }}>{error}</p>}
      <form onSubmit={handleSubmit}>
        <div>
          <label>Username:</label>
          <input
            type="text"
            name="username"
            value={formData.username}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Email:</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Password:</label>
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit">Register</button>
      </form>
      <p>
        Already have an account?{" "}
        <span
          onClick={() => history.push("/login")}
          style={{ cursor: "pointer", color: "blue" }}
        >
          Login here
        </span>
      </p>
    </div>
  );
};

export default Register;
