import React, { useState, useContext } from "react";
import { useNavigate } from "react-router-dom"; // Updated to useNavigate (v6)
import { AuthContext } from "../contexts/AuthContext";
import { registerUser } from "../api/auth";

const Register = () => {
  const [formData, setFormData] = useState({
    username: "",
    email: "",
    password: "",
  });

  const [error, setError] = useState("");

  // We use 'login' here to auto-authenticate the user if the backend returns a token
  // If your AuthContext calls it 'setAuth', feel free to change it back,
  // but 'login' matches the standard setup we discussed.
  const { login } = useContext(AuthContext);

  const navigate = useNavigate(); // Updated hook

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(""); // Clear previous errors
    try {
      // Call the API
      const data = await registerUser(formData);

      // 1. If your backend sends a token immediately upon register:
      if (data.token) {
        login(data.token); // Save token to context
        navigate("/"); // Redirect to Home (Product List) as per assignment
      } else {
        // 2. If backend just says "Success" but requires manual login:
        navigate("/login");
      }
    } catch (err) {
      // robust error handling
      setError(
        err.response?.data?.message || "Registration failed. Try again."
      );
    }
  };

  return (
    <div>
      <h2>Register</h2>
      {error && <p style={{ color: "red" }}>{error}</p>}
      <form onSubmit={handleSubmit}>
        <div>
          <label>Username:</label>
          <input
            type="text"
            name="username"
            value={formData.username}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Email:</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Password:</label>
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit">Register</button>
      </form>
      <p>
        Already have an account?{" "}
        <span
          onClick={() => navigate("/login")}
          style={{ cursor: "pointer", color: "blue" }}
        >
          Login here
        </span>
      </p>
    </div>
  );
};

export default Register;
