import React, { useEffect, useState } from "react";
import { useParams, useHistory } from "react-router-dom"; // 1. Added useHistory
import axios from "axios";
import { addToCart } from "../api/cart"; // 2. Import the API helper
import "./ProductDetails.css";

const ProductDetails = () => {
  const { id } = useParams();
  const history = useHistory(); // 3. Initialize history
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [adding, setAdding] = useState(false); // 4. State for button loading

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await axios.get(
          `http://localhost:5000/api/products/${id}`
        );
        setProduct(response.data);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching product:", error);
        setLoading(false);
      }
    };

    fetchProduct();
  }, [id]);

  // 5. Logic to handle Add to Cart click
  const handleAddToCart = async () => {
    const token = localStorage.getItem("token");

    // Check if user is logged in
    if (!token) {
      alert("You must be logged in to add items to the cart!");
      history.push("/login"); // Redirect to login if not authenticated
      return;
    }

    try {
      setAdding(true); // Show loading state on button
      await addToCart(product._id, 1); // Call the API helper (default quantity: 1)
      alert("Item added to cart successfully!");
      setAdding(false);
    } catch (error) {
      console.error("Error adding to cart:", error);
      alert("Failed to add item. Please try again.");
      setAdding(false);
    }
  };

  if (loading)
    return (
      <div className="product-details-container">
        <h2>Loading...</h2>
      </div>
    );

  if (!product)
    return (
      <div className="product-details-container">
        <h2>Product not found</h2>
      </div>
    );

  return (
    <div className="product-details-container">
      {/* Left Side: Image */}
      <div className="product-image-section">
        <img
          src={product.imageUrl}
          alt={product.name}
          onError={(e) => {
            e.target.src = "https://via.placeholder.com/400";
          }}
        />
      </div>

      {/* Right Side: Details */}
      <div className="product-info-section">
        <h1 className="product-title">{product.name}</h1>
        <p className="product-category">Category: {product.category}</p>
        <p className="product-price">${product.price}</p>

        <div className="product-description">
          <h3>Description:</h3>
          <p>{product.description}</p>
        </div>

        {/* 6. Updated Button with Handler */}
        <button
          className="add-to-cart-btn"
          onClick={handleAddToCart}
          disabled={adding} // Disable while adding to prevent double clicks
          style={adding ? { opacity: 0.7, cursor: "not-allowed" } : {}}
        >
          {adding ? "Adding..." : "Add to Cart"}
        </button>
      </div>
    </div>
  );
};

export default ProductDetails;
