// 1. LOAD .ENV FIRST (Check specific path since file is in 'src')
const path = require("path");
require("dotenv").config({ path: path.resolve(__dirname, "../.env") });

const mongoose = require("mongoose");
const app = require("./app"); // Importing your express app setup

// 2. CHECK IF VARS LOADED (Debug log)
console.log("Loading .env file...");
console.log("MONGO_URI:", process.env.MONGO_URI);

// 3. CONNECT TO DATABASE
const connectDB = async () => {
  try {
    await mongoose.connect(process.env.MONGO_URI, {
      // These options are no longer needed in Mongoose 6+, but keep if on v5
      useNewUrlParser: true,
      useUnifiedTopology: true,
    });
    console.log("MongoDB connected successfully");
  } catch (error) {
    console.error("MongoDB connection failed:", error.message);
    process.exit(1);
  }
};

connectDB();

// 4. START SERVER
const PORT = process.env.PORT || 5000;
app.listen(PORT, () => {
  console.log(`Server running on port ${PORT}`);
});
